#!/bin/bashset -eecho "🔧 Starting FastAPI setup..."python3 -m pip install --upgrade pipif [ -f requirements.txt ]; then    pip install -r requirements.txtelse    echo "⚠️ No requirements.txt found, skipping dependency install."fiecho "🚀 Launching FastAPI with Uvicorn..."exec python -m uvicorn app.main:app --host 0.0.0.0 --port 8000